export default'/**\r\n * 表单全局属性配置\r\n */\r\nimport Vue from "vue";\r\nimport { MlForm, MlFormConfig } from "types/form";\r\n\r\nexport default Vue.extend({\r\n  name: "FormRule",\r\n  data() {\r\n    return {\r\n      formConfig: null as MlFormConfig,\r\n      formValue: null,\r\n      setConfig: null as MlFormConfig,\r\n      setValue: null,\r\n    };\r\n  },\r\n  created() {\r\n    this.formConfig = {\r\n      columns: [\r\n        {\r\n          type: "input",\r\n          label: "姓名",\r\n          prop: "name",\r\n        },\r\n        {\r\n          type: "input",\r\n          label: "长度",\r\n          prop: "length",\r\n        },\r\n        {\r\n          type: "select",\r\n          label: "性别",\r\n          prop: "type",\r\n          required: true,\r\n          options: [\r\n            { value: 1, label: "男" },\r\n            { value: 0, label: "女" },\r\n          ],\r\n        },\r\n      ],\r\n    };\r\n    this.setConfig = {\r\n      inline: false,\r\n      columns: [\r\n        {\r\n          type: "radio",\r\n          label: "表单样式",\r\n          prop: "uiType",\r\n          options: [\r\n            { value: "line", label: "line" },\r\n            { value: "round", label: "round" },\r\n            { value: "border", label: "border" },\r\n          ],\r\n        },\r\n        {\r\n          type: "radio",\r\n          label: "行内表单",\r\n          prop: "inline",\r\n          options: [\r\n            { label: "是", value: true },\r\n            { label: "否", value: false },\r\n          ],\r\n        },\r\n        {\r\n          type: "radio",\r\n          label: "标签位置",\r\n          prop: "labelPosition",\r\n          options: [\r\n            { value: "left", label: "左对齐" },\r\n            { value: "right", label: "右对齐" },\r\n            { value: "top", label: "顶部对齐" },\r\n          ],\r\n        },\r\n      ],\r\n    };\r\n  },\r\n  mounted() {\r\n    this.$watch(\r\n      "setValue",\r\n      () => {\r\n        this.formConfig = { ...this.formConfig, ...this.setValue };\r\n      },\r\n      { deep: true },\r\n    );\r\n  },\r\n  methods: {\r\n    onSubmit() {\r\n      let form = this.$refs.form as MlForm;\r\n      form.validate();\r\n    },\r\n  },\r\n  render() {\r\n    return (\r\n      <div>\r\n        <ml-form ref="form" config={this.setConfig} v-model={this.setValue}></ml-form>\r\n        <ml-form ref="form" config={this.formConfig} v-model={this.formValue}></ml-form>\r\n      </div>\r\n    );\r\n  },\r\n});\r\n';
